"use client";
import React, { useEffect, useState } from "react";
import {
    Avatar,
    Button,
    Card,
    Col,
    Empty,
    Flex,
    Pagination,
    Rate,
    Row,
    Skeleton,
    Space,
    Typography,
    theme,
    Image,
    Tag,
    Modal,
} from "antd";
import {
    UserOutlined,
    MessageOutlined,
    StarFilled,
    ExclamationCircleOutlined,
    OrderedListOutlined,
} from "@ant-design/icons";
import Link from "next/link";
import { useRouter } from "next/navigation";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import ClientUserNavbar from "@/components/ClientUserNavbar";
import useTitle from "@/hooks/use-title";
import FetchUtils, { ErrorMessage, ListResponse } from "@/utils/FetchUtils";
import ResourceURL from "@/constants/ResourceURL";
import NotifyUtils from "@/utils/NotifyUtils";
import ApplicationConstants from "@/constants/ApplicationConstants";

import DateUtils from "@/utils/DateUtils";
import { ClientReviewResponse } from "@/datas/ClientUI";

const { Title, Text, Paragraph, Link: AntLink } = Typography;
const { useToken } = theme;

function ClientReview() {
    useTitle("Đánh giá của tôi");
    const { token } = useToken();
    const [activePage, setActivePage] = useState(1);

    const {
        reviewResponses,
        isLoadingReviewResponses,
        isErrorReviewResponses,
    } = useGetAllReviewsApi(activePage);
    const reviews = reviewResponses as ListResponse<ClientReviewResponse>;

    let reviewContentFragment;

    if (isLoadingReviewResponses) {
        reviewContentFragment = (
            <Space direction="vertical" style={{ width: "100%" }}>
                {Array(5)
                    .fill(0)
                    .map((_, index) => (
                        <Skeleton key={index} active paragraph={{ rows: 2 }} />
                    ))}
            </Space>
        );
    } else if (isErrorReviewResponses) {
        reviewContentFragment = (
            <Flex
                vertical
                align="center"
                style={{
                    margin: `${token.marginXL}px 0`,
                    color: token.colorError,
                }}
            >
                <ExclamationCircleOutlined style={{ fontSize: 80 }} />
                <Text style={{ fontSize: 18, fontWeight: 500, marginTop: 16 }}>
                    Đã có lỗi xảy ra
                </Text>
            </Flex>
        );
    } else if (reviews && reviews.totalElements === 0) {
        reviewContentFragment = (
            <Empty
                image={
                    <OrderedListOutlined
                        style={{ fontSize: 80, color: token.colorPrimary }}
                    />
                }
                description={
                    <Text style={{ fontSize: 18, fontWeight: 500 }}>
                        Bạn chưa đánh giá sản phẩm nào
                    </Text>
                }
                style={{ margin: `${token.marginXL}px 0` }}
            />
        );
    } else if (reviews && reviews.totalElements > 0) {
        reviewContentFragment = (
            <>
                <Space
                    direction="vertical"
                    size="large"
                    style={{ width: "100%" }}
                >
                    {reviews.content.map((review) => (
                        <ClientReviewCard
                            key={review.reviewId}
                            review={review}
                        />
                    ))}
                </Space>

                <Flex
                    justify="space-between"
                    align="center"
                    style={{ marginTop: 24 }}
                >
                    <Pagination
                        current={activePage}
                        total={reviews.totalElements}
                        pageSize={
                            ApplicationConstants.DEFAULT_CLIENT_USER_REVIEW_PAGE_SIZE
                        }
                        onChange={(page) =>
                            page !== activePage && setActivePage(page)
                        }
                        showSizeChanger={false}
                    />
                    <Text>
                        <Text strong>Trang {activePage}</Text>
                        <span> / {reviews.totalPages}</span>
                    </Text>
                </Flex>
            </>
        );
    }

    return (
        <main>
            <div className="container mx-auto px-4" style={{ maxWidth: 1200 }}>
                <Row gutter={[24, 24]}>
                    <Col xs={24} md={6}>
                        <ClientUserNavbar />
                    </Col>

                    <Col xs={24} md={18}>
                        <Card
                            bordered={false}
                            style={{ borderRadius: token.borderRadiusLG }}
                        >
                            <Space
                                direction="vertical"
                                size="large"
                                style={{ width: "100%" }}
                            >
                                <Title level={2}>Đánh giá của tôi</Title>
                                {reviewContentFragment}
                            </Space>
                        </Card>
                    </Col>
                </Row>
            </div>
        </main>
    );
}

function ClientReviewCard({ review }: { review: ClientReviewResponse }) {
    const { token } = useToken();
    const deleteReviewApi = useDeleteReviewApi();
    const router = useRouter();

    const handleDeleteReview = () => {
        Modal.confirm({
            title: "Xác nhận xóa",
            icon: <ExclamationCircleOutlined />,
            content: (
                <Text>
                    Xóa đánh giá sản phẩm{" "}
                    <Text strong>{review.reviewProduct.productName}</Text>?
                </Text>
            ),
            okText: "Xóa",
            okType: "danger",
            cancelText: "Không xóa",
            onOk: () => deleteReviewApi.mutate(review.reviewId),
        });
    };

    const handleViewAllReviews = () => {
        router.push(`/product/${review.reviewProduct.productSlug}#reviews`);
    };

    return (
        <Card
            style={{ borderRadius: token.borderRadiusLG }}
            bodyStyle={{ padding: "16px 20px" }}
        >
            <Space direction="vertical" size="middle" style={{ width: "100%" }}>
                <Flex justify="space-between" align="center" wrap="wrap">
                    <Flex align="center" gap={12}>
                        <Avatar icon={<UserOutlined />} />
                        <Space direction="vertical" size={4}>
                            <Text strong>
                                {review.reviewProduct.productName}
                            </Text>
                            <Text type="secondary" style={{ fontSize: 13 }}>
                                {DateUtils.isoDateToString(
                                    review.reviewCreatedAt,
                                )}
                            </Text>
                        </Space>
                    </Flex>
                    <Space>
                        <Rate
                            disabled
                            value={review.reviewRatingScore}
                            character={<StarFilled style={{ fontSize: 16 }} />}
                        />
                        <Tag color="blue">{review.reviewRatingScore}/5</Tag>
                    </Space>
                </Flex>

                <Link
                    href={`/product/${review.reviewProduct.productSlug}`}
                    passHref
                    legacyBehavior
                >
                    <AntLink>
                        <Flex align="center" gap={12}>
                            <Image
                                width={40}
                                height={40}
                                src={
                                    review.reviewProduct.productThumbnail || ""
                                }
                                alt={review.reviewProduct.productName}
                                preview={false}
                                style={{ borderRadius: token.borderRadius }}
                                fallback="data:image/png;base64,"
                            />
                            <Text strong>
                                {review.reviewProduct.productName}
                            </Text>
                        </Flex>
                    </AntLink>
                </Link>

                <Paragraph
                    style={{ margin: 0 }}
                    ellipsis={{ rows: 3, expandable: true, symbol: "Xem thêm" }}
                >
                    {review.reviewContent}
                </Paragraph>

                <Space>
                    <Button
                        type="primary"
                        icon={<MessageOutlined />}
                        onClick={handleViewAllReviews}
                    >
                        Xem tất cả đánh giá
                    </Button>
                    <Button danger onClick={handleDeleteReview}>
                        Xóa đánh giá
                    </Button>
                </Space>
            </Space>
        </Card>
    );
}

function useGetAllReviewsApi(activePage: number) {
    const requestParams = {
        page: activePage,
        size: ApplicationConstants.DEFAULT_CLIENT_USER_REVIEW_PAGE_SIZE,
    };

    const {
        data: reviewResponses,
        isLoading: isLoadingReviewResponses,
        isError: isErrorReviewResponses,
    } = useQuery<ListResponse<ClientReviewResponse>, ErrorMessage>({
        queryKey: ["client-api", "reviews", "getAllReviews", requestParams],
        queryFn: () =>
            FetchUtils.getWithToken(ResourceURL.CLIENT_REVIEW, requestParams),

        refetchOnWindowFocus: false,
        placeholderData: (previousData) => previousData,
    });

    useEffect(() => {
        if (isErrorReviewResponses) {
            NotifyUtils.simpleFailed("Lấy dữ liệu không thành công");
        }
    }, [isErrorReviewResponses]);

    return {
        reviewResponses,
        isLoadingReviewResponses,
        isErrorReviewResponses,
    };
}

function useDeleteReviewApi() {
    const queryClient = useQueryClient();

    return useMutation<void, ErrorMessage, number>({
        mutationFn: (reviewId) =>
            FetchUtils.deleteWithToken(
                ResourceURL.CLIENT_REVIEW + "/" + reviewId,
                [],
            ),

        onSuccess: () => {
            NotifyUtils.simpleSuccess("Xóa đánh giá thành công");
            void queryClient.invalidateQueries({
                queryKey: ["client-api", "reviews", "getAllReviews"],
            });
        },
        onError: () =>
            NotifyUtils.simpleFailed("Xóa đánh giá không thành công"),
    });
}

export default ClientReview;
